import {Pagination} from "@mui/material";
import {Card} from "../../components/Card";
import {useEffect, useState} from "react";
import axios from "axios";
import {useSearchParams} from "react-router-dom";
import "./boardList.scss";
import moment from "moment";

const BoardList = () => {
  const [pageCount, setPageCount] = useState(0);
  const [boardList, setBoardList] = useState([]);
  const [searchParams, setSearchParams] = useSearchParams();

  // Î†åÎçîÎßÅ ÎêòÍ≥† ÌïúÎ≤àÎßå Ï†ÑÏ≤¥ Í≤åÏãúÎ¨º Í∞ØÏàò Í∞ÄÏ†∏ÏôÄÏÑú ÌéòÏù¥ÏßÄ Ïπ¥Ïö¥Ìä∏ Íµ¨ÌïòÍ∏∞
  // Î†åÎçîÎßÅ ÎêòÍ≥† ÌïúÎ≤àÎßå ÌéòÏù¥ÏßÄÏóê Ìï¥ÎãπÌïòÎäî Í≤åÏãúÎ¨º Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    // ÌéòÏù¥ÏßÄÏóê Ìï¥ÎãπÌïòÎäî Í≤åÏãúÎ¨º Í∞ÄÏ†∏Ïò§Í∏∞
    const getBoardList = async () => {
      const page_number = searchParams.get("page");
      const {data} = await axios.get(`/api/board/list?page_number=${page_number}&page_size=4`);
      return data;
    }
    // ÌòÑÏû¨ ÌéòÏù¥ÏßÄÏóê Ìï¥ÎãπÌïòÎäî Í≤åÏãúÎ¨ºÎ°ú ÏÉÅÌÉú Î≥ÄÍ≤ΩÌïòÍ∏∞
    getBoardList().then(result => setBoardList(result));
    // Í≤åÏãúÎ¨º Ï†ÑÏ≤¥ Í∞ØÏàò Íµ¨ÌïòÍ∏∞
    const getTotalBoard = async () => {
      const {data} = await axios.get("/api/board/count");
      return data.total;
    }
    // ÌéòÏù¥ÏßÄ Ïπ¥Ïö¥Ìä∏ Íµ¨ÌïòÍ∏∞: (Ï†ÑÏ≤¥ board Í∞ØÏàò) / (Ìïú ÌéòÏù¥ÏßÄ Í∞ØÏàò) Í≤∞Í≥º Ïò¨Î¶º
    getTotalBoard().then(result => setPageCount(Math.ceil(result / 4)));
  }, [])

  return (
    <div className="boardList-wrapper">
      <div className="boardList-header">
        Ï†ÑÏ≤¥ Í≤åÏãúÎ¨º üìù
      </div>
      <div className="boardList-body">
        {boardList.map((item, index) => (
          <Card key={item.id} username={item.user.username}
                date={moment(item.created).add(9, "hour").format('YYYY-MM-DD')}
                title={item.title} content={item.content}
                board_id={item.id} img_url={`/api/image/view/${item.id}`}
          />
        ))}
      </div>
      <div className="boardList-footer">
        {/*ÌéòÏù¥ÏßÄÎÑ§Ïù¥ÏÖò: countÏóê ÌéòÏù¥ÏßÄ Ïπ¥Ïö¥Ìä∏, pageÏóê ÌéòÏù¥ÏßÄ Î≤àÌò∏ ÎÑ£Í∏∞*/}
        <Pagination
          variant="outlined" color="primary" page={Number(searchParams.get("page"))}
          count={pageCount} size="large"
          onChange={(e, value) => {
            window.location.href = `/board-list?page=${value}`;
          }}
          showFirstButton showLastButton
        />
      </div>
    </div>

  )
}
export default BoardList;
