import React, {useEffect, useState} from "react";
import axios from "axios";
import {useNavigate, useParams} from "react-router-dom";
import "./board.scss";
import {jwtUtils} from "../../utils/jwtUtils";
import {Button, Dialog, DialogContent, IconButton} from "@mui/material";
import {useSelector} from "react-redux";
import BuildOutlinedIcon from '@mui/icons-material/BuildOutlined';
import DeleteForeverOutlinedIcon from '@mui/icons-material/DeleteForeverOutlined';
import DisabledByDefaultOutlinedIcon from "@mui/icons-material/DisabledByDefaultOutlined";
import api from "../../utils/api";
import moment from "moment";
import Comments from "../../components/Comments";

const Board = () => {
  // URL ÌååÎùºÎØ∏ÌÑ∞ Î∞õÍ∏∞ - boardÏùò id
  const {board_id} = useParams();
  const [board, setBoard] = useState({});
  const [isLoaded, setIsLoaded] = useState(false);
  const token = useSelector(state => state.Auth.token);
  const navigate = useNavigate();
  // modalÏù¥ Î≥¥Ïù¥Îäî Ïó¨Î∂Ä ÏÉÅÌÉú
  const [show, setShow] = useState(false);
  // board Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    const getBoard = async () => {
      const {data} = await axios.get(`/api/board/${board_id}`);
      return data;
    }
    getBoard().then(result => setBoard(result)).then(() => setIsLoaded(true));
  }, [])
  return (
    <React.Fragment>
      {isLoaded && (
        <div className="board-wrapper">
          {
            /*
              Ìï¥Îãπ Í∏ÄÏùò ÏûëÏÑ±ÏûêÍ∞Ä Î°úÍ∑∏Ïù∏ÏùÑ ÌñàÏùÑ ÎïåÎßå ÏàòÏ†ï, ÏÇ≠Ï†ú Î≤ÑÌäºÏù¥ Î≥¥Ïù¥Í≤å ÌïòÏûê.
              Î°úÍ∑∏Ïù∏ÏùÑ Ìïú ÏÇ¨Ïö©ÏûêÏùò jwt-tokenÏóêÏÑú userÏùò IDÎ•º Ï∂îÏ∂úÌïú ÌõÑ,
              board(Ìï¥Îãπ Í∏Ä)Ïùò userÏùò IDÎ•º ÎπÑÍµêÌñàÏùÑ Îïå Í∞ôÏúºÎ©¥ ÏàòÏ†ï, ÏÇ≠Ï†ú Î≤ÑÌäºÏù¥ Î≥¥Ïù¥Í≤å ÌïúÎã§.
              IDÎäî DBÏóê Ï†ÄÏû•ÎêòÏñ¥ ÏûàÎäî Ïú†Ï†ÄÏùò Í≥†Ïú† Î≤àÌò∏Ïù¥Îã§.
             */
            jwtUtils.isAuth(token) && jwtUtils.getId(token) === board.user.id &&
            <div className="edit-delete-button">
              <Button
                variant="outlined" color="error" endIcon={<DeleteForeverOutlinedIcon/>}
                className="delete-button"
                onClick={() => {
                  setShow(true)
                }}
              >
                ÏÇ≠Ï†ú
              </Button>
              <Button
                variant="outlined" endIcon={<BuildOutlinedIcon/>}
                onClick={() => {
                  navigate(`/edit-board/${board_id}`)
                }}
              >
                ÏàòÏ†ï
              </Button>
            </div>
          }
          <div className="board-header">
            <div className="board-header-username">{board.user.username}</div>
            <div className="board-header-date">{moment(board.created).add(9, "hour").format('YYYY-MM-DD')}</div>
          </div>
          <hr/>
          <div className="board-body">
            <div className="board-image">
              <img src={`/api/image/view/${board_id}`}/>
            </div>
            <div className="board-title-content">
              <div className="board-title">{board.title}</div>
              <div className="board-content">{board.content}</div>
            </div>
          </div>
          <hr/>
          <div className="board-footer">
            <Comments board_id={board_id}/>
          </div>
        </div>
      )}
      {/*modal*/}
      <Dialog open={show}>
        <DialogContent style={{position: "relative"}}>
          <IconButton
            style={{position: "absolute", top: "0", right: "0"}}
            onClick={() => setShow(false)}
          >
            <DisabledByDefaultOutlinedIcon/>
          </IconButton>
          <div className="modal">
            <div className="modal-title"> Ï†ïÎßê ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå ?</div>
            <div className="modal-button">
              <Button
                variant="outlined"
                color="error"
                onClick={async () => {
                  setShow(false);
                  // Î™®Îã¨Ïùò Ïòà Î≤ÑÌäº ÌÅ¥Î¶≠Ïãú Í≤åÏãúÎ¨º ÏÇ≠Ï†ú
                  await api.delete(`/api/board/${board_id}`);
                  alert("Í≤åÏãúÎ¨ºÏù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§üòé");
                  window.location.href = "/myboard-list";
                }}
              >
                Ïòà
              </Button>
              <Button
                variant="outlined"
                color="primary"
                onClick={() => {
                  setShow(false)
                }}
              >
                ÏïÑÎãàÏò§
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </React.Fragment>
  );
}
export default Board;
