            toast.error("Ïò§Î•òÎ∞úÏÉù! Ïù¥Î™®ÏßÄÎ•º ÏÇ¨Ïö©ÌïòÎ©¥ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌï† Ïàò ÏûàÏäµÎãàÎã§" + "üò≠", {
                position: "top-center",
            });
        }

    }, [canSubmit]);

    return (
        <div>

            <div className="addBoard-wrapper">
                <div className="addBoard-header">
                    Í≤åÏãúÎ¨º Îì±Î°ùÌïòÍ∏∞ üñäÔ∏è
                </div>
                <div className="submitButton">
                    {canSubmit() ? (
                        <Button
                            onClick={handleSubmit}
                            className="success-button"
                            variant="outlined"
                        >
                            Îì±Î°ùÌïòÍ∏∞üòÉ
                        </Button>
                    ) : (
                        <Button
                            className="disable-button"
                            variant="outlined"
                            size="large"
                        >
                            ÏÇ¨ÏßÑÍ≥º ÎÇ¥Ïö©ÏùÑ Î™®Îëê ÏûÖÎ†•ÌïòÏÑ∏Ïöîüò≠
                        </Button>
                    )}
                </div>
                <div className="addBoard-body">
                    <ImageUploader setImage={setImage} preview_URL={image.preview_URL}/>
                    <TextArea setTitle={setTitle} setContent={setContent} title={title} content={content}/>
                </div>
            </div>
        </div>
    );
}

export default AddBoard;
